// Mode: Run Once for All Items
const item = $input.first();
const src = item.json || {};
const body = (src && typeof src.body === 'object') ? src.body : null;

// ç»Ÿä¸€å–å€¼ï¼šä¼˜å…ˆ bodyï¼Œå…¶æ¬¡é¡¶å±‚ json
const get = (k, d) => (body?.[k] !== undefined ? body[k] : (src[k] !== undefined ? src[k] : d));
const sanitize = (s) => (s == null ? '' : String(s)).trim();

// count é™åˆ¶ 1~6ï¼Œé»˜è®¤ 3
const rawCount = get('count', 3);
const n = parseInt(rawCount, 10);
const count = Math.max(1, Math.min(6, Number.isFinite(n) ? n : 3));

// ä¾›å·²æœ‰èŠ‚ç‚¹å¼•ç”¨çš„â€œè§„èŒƒåŒ–å­—æ®µâ€ï¼ˆå¤§å†™å‘½åï¼‰
const normalized = {
  Keywords: sanitize(get('keywords', '')),
  Audience: sanitize(get('audience', '')),
  'Count of cards': count,
  Style: sanitize(get('style', 'Doodle-style')),
};

// è¿”å›ï¼šä¿ç•™åŸå§‹ jsonï¼ˆå« body / å…¶ä»–å­—æ®µï¼‰ï¼Œè¡¥å……é¡¶å±‚å°å†™å­—æ®µ + è§„èŒƒåŒ–å­—æ®µ
return [{
  json: {
    ...src,                    // åŸå§‹ç»“æ„å…¨ä¿ç•™
    // é¡¶å±‚å°å†™é”®ï¼ˆæ–¹ä¾¿æœ‰çš„èŠ‚ç‚¹ç”¨å°å†™å–å€¼ï¼‰
    keywords: get('keywords', ''),
    audience: get('audience', ''),
    count,
    style: get('style', get('Style', 'Doodle-style')),
    // æ ‡å‡†å­—æ®µï¼ˆä¸ä½ åç»­ Message a model / Edit Image ç­‰ç”¨æ³•ä¸€è‡´ï¼‰
    ...normalized,
  },
  binary: item.binary,         // è‹¥ä¸Šæ¸¸å¸¦æœ‰äºŒè¿›åˆ¶ï¼Œè¿™é‡Œä¸ä¸¢
}];

// å…¼å®¹å››ç§å¸¸è§è·¯å¾„ï¼Œæ”¶é›†å‡ºå½“å‰ item é‡Œçš„æ‰€æœ‰ data URL
function collectUrls(j) {
  // 1) è‡ªå·±æ„é€ çš„æ•°ç»„ï¼šimages: [{type:'image_url', image_url:{url:'data:...'}}, ...]
  if (Array.isArray(j.images)) {
    return j.images.map(x => x?.image_url?.url ?? x?.url).filter(Boolean);
  }
  // 2) OpenRouter é£æ ¼ï¼šchoices[0].message.images
  if (Array.isArray(j.choices?.[0]?.message?.images)) {
    return j.choices[0].message.images.map(x => x?.image_url?.url ?? x?.url).filter(Boolean);
  }
  // 3) content æ··åˆæ•°ç»„ï¼š[{type:'text'}, {type:'image_url', image_url:{url:'data:...'}} ...]
  if (Array.isArray(j.choices?.[0]?.message?.content)) {
    return j.choices[0].message.content
      .filter(x => x?.type === 'image_url')
      .map(x => x?.image_url?.url ?? x?.url)
      .filter(Boolean);
  }
  // 4) Gemini é£æ ¼ (input2.json): candidates[0].content.parts
  if (Array.isArray(j.candidates?.[0]?.content?.parts)) {
    return j.candidates[0].content.parts
      .filter(p => p.inlineData && p.inlineData.data)
      .map(p => `data:${p.inlineData.mimeType};base64,${p.inlineData.data}`)
      .filter(Boolean);
  }
  return [];
}


const inputItems = $input.all();
const out = [];

for (const [itemIdx, it] of inputItems.entries()) {
  const urls = collectUrls(it.json);

  for (let i = 0; i < urls.length; i++) {
    const url = urls[i] || '';
    const m = url.match(/^data:([^;]+);base64,(.+)$/s);
    if (!m) {
      out.push({ json: { itemIdx, index: i, error: 'Not a valid data URL', url } });
      continue;
    }

    const mime = m[1];
    const b64  = m[2];
    const buf  = Buffer.from(b64, 'base64');
    const ext  = (mime.split('/')[1] || 'bin').replace(/[^a-z0-9]/gi, '');
    const fileName = `image_${String(i + 1).padStart(2, '0')}.${ext}`;

    // å…³é”®ï¼šè¦ awaitï¼
    const bin = await this.helpers.prepareBinaryData(buf, fileName, mime);

    out.push({
      json:   { itemIdx, index: i, mime, fileName },
      binary: { data: bin }   // ç°åœ¨ Binary é¢æ¿ä¼šå‡ºç°å¯ View/Download çš„å¡ç‰‡
    });
  }
}

return out;

// ç­›é€‰å‡ºåŒ…å«bottom binaryæ•°æ®çš„items
const bottomImages = $input.all().filter(item => {
  // æ£€æŸ¥æ˜¯å¦æœ‰bottomè¿™ä¸ªbinary key
  return item.binary && item.binary.bottom;
});

console.log("ç­›é€‰å‡ºçš„bottomå›¾ç‰‡æ•°é‡:", bottomImages.length);

// è½¬æ¢ä¸ºå‰ç«¯æœŸæœ›çš„JSONæ ¼å¼
const jsonResults = [];

for (let idx = 0; idx < bottomImages.length; idx++) {
  const item = bottomImages[idx];
  const binary = item.binary.bottom;

  // ä½¿ç”¨å®˜æ–¹æ–¹æ³•è·å–äºŒè¿›åˆ¶æ•°æ®ï¼Œå…¼å®¹filesystemæ¨¡å¼
  const binaryBuffer = await this.helpers.getBinaryDataBuffer(idx, 'bottom');
  const base64Data = binaryBuffer.toString('base64');

  const fileName = binary.fileName || `image_${idx + 1}.png`;

  jsonResults.push({
    data_url: `data:${binary.mimeType};base64,${base64Data}`,
    output_filename: fileName,
    mime: binary.mimeType,
    chunks: [base64Data],
    index: idx,
    total: bottomImages.length
  });
}

// è¿”å›åŒ…å«final_resultsçš„å¯¹è±¡ï¼Œä¾›webhookè¿”å›
return [{
  json: {
    final_results: jsonResults,
    total_count: bottomImages.length,
    status: 'completed'
  }
}];

