function safeName(name, fallback = 'image.png') {
  const n = (name || '').toString().trim();
  return (n ? n : fallback).replace(/[^A-Za-z0-9._-]/g, '_');
}

// å…¼å®¹ä¸¤ç§è¿”å›ï¼šç›´æ¥ data_url æˆ– {mime, chunks[]}
function buildDataURL(item) {
  if (item.data_url) return item.data_url;
  if (item.mime && Array.isArray(item.chunks)) {
    const b64 = item.chunks.join('');
    return `data:${item.mime};base64,${b64}`;
  }
  return null;
}

// å…¨å±€å˜é‡å­˜å‚¨ç”Ÿæˆçš„å›¾ç‰‡
let generatedImages = [];

// æ™ºèƒ½ç½‘æ ¼å¸ƒå±€é…ç½®ï¼ˆ1-6å¼ å›¾ç‰‡ï¼‰
function getGridLayout(count) {
  const layouts = {
    1: { columns: '1fr', rows: 'auto', maxWidth: '280px' },
    2: { columns: 'repeat(2, 1fr)', rows: 'auto', maxWidth: '100%' },
    3: { columns: 'repeat(2, 1fr)', rows: 'auto auto', maxWidth: '100%' }, // 2+1å¸ƒå±€
    4: { columns: 'repeat(2, 1fr)', rows: 'repeat(2, auto)', maxWidth: '100%' },
    5: { columns: 'repeat(2, 1fr)', rows: 'auto auto auto', maxWidth: '100%' }, // 2+2+1å¸ƒå±€
    6: { columns: 'repeat(2, 1fr)', rows: 'repeat(3, auto)', maxWidth: '100%' }
  };
  return layouts[Math.min(count, 6)] || layouts[6];
}

// åœ¨é¢„è§ˆåŒºæ˜¾ç¤ºå›¾ç‰‡ï¼ˆå³ä¾§ï¼‰
function updatePreview(images) {
  const previewContent = document.querySelector('.preview-content');
  previewContent.innerHTML = '';

  if (images.length === 0) {
    previewContent.innerHTML = `
      <div class="preview-placeholder">
        <div class="preview-icon">ğŸ´</div>
        <div class="preview-text">
          å¡«å†™å·¦ä¾§è¡¨å•<br>
          å¼€å§‹åˆ›ä½œæ‚¨çš„ä¸“å±çŸ¥è¯†å¡ç‰‡
        </div>
      </div>
    `;
    return;
  }

  const count = images.length;
  const layout = getGridLayout(count);

  // åˆ›å»ºæ™ºèƒ½ç½‘æ ¼å®¹å™¨
  const imageGrid = document.createElement('div');
  imageGrid.style.cssText = `
    display: grid;
    grid-template-columns: ${layout.columns};
    grid-template-rows: ${layout.rows};
    gap: 12px;
    width: 100%;
    max-width: ${layout.maxWidth};
    margin: 0 auto;
    max-height: 500px;
    overflow-y: auto;
    align-content: start;
  `;

  images.forEach((it, idx) => {
    const dataUrl = buildDataURL(it);
    if (!dataUrl) return;
    const fname = safeName(it.output_filename || `image_${idx + 1}.png`);

    const imageContainer = document.createElement('div');
    imageContainer.style.cssText = `
      background: var(--panel-light);
      border: 1px solid var(--border-light);
      border-radius: 12px;
      overflow: hidden;
      transition: all 0.3s ease;
      animation: fadeInScale 0.5s ease-out;
      aspect-ratio: 3/4;
      position: relative;
    `;

    // ç‰¹æ®Šå¸ƒå±€å¤„ç†
    if (count === 3 && idx === 2) {
      // ç¬¬3å¼ å›¾ç‰‡è·¨ä¸¤åˆ—å±…ä¸­
      imageContainer.style.gridColumn = '1 / -1';
      imageContainer.style.maxWidth = '280px';
      imageContainer.style.margin = '0 auto';
    } else if (count === 5 && idx === 4) {
      // ç¬¬5å¼ å›¾ç‰‡è·¨ä¸¤åˆ—å±…ä¸­
      imageContainer.style.gridColumn = '1 / -1';
      imageContainer.style.maxWidth = '280px';
      imageContainer.style.margin = '0 auto';
    }

    imageContainer.innerHTML = `
      <img src="${dataUrl}" alt="${fname}" 
           style="width: 100%; height: 100%; object-fit: cover; display: block;" />

      <!-- å³ä¸Šè§’ä¸‹è½½æŒ‰é’® -->
      <button class="download-btn" 
              style="position: absolute; top: 8px; right: 8px; 
                     width: 32px; height: 32px; border-radius: 50%; 
                     background: rgba(0, 0, 0, 0.8); border: none; 
                     color: white; cursor: pointer; 
                     display: flex; align-items: center; justify-content: center;
                     font-size: 14px; transition: all 0.2s ease;
                     backdrop-filter: blur(4px);"
              title="ä¸‹è½½å›¾ç‰‡">
        â¬‡
      </button>

      <!-- åº•éƒ¨è¿›åº¦æ˜¾ç¤º -->
      <div style="position: absolute; bottom: 0; left: 0; right: 0; background: rgba(0,0,0,0.7); color: white; padding: 4px 8px; font-size: 11px; text-align: center;">
        ${idx + 1}/${images.length}
      </div>
    `;

    // å…ˆæ·»åŠ åˆ°DOM
    imageGrid.appendChild(imageContainer);

    // ç„¶åä¸ºä¸‹è½½æŒ‰é’®æ·»åŠ äº‹ä»¶ç›‘å¬å™¨
    const downloadBtn = imageContainer.querySelector('.download-btn');
    if (downloadBtn) {
      console.log('ç»‘å®šä¸‹è½½æŒ‰é’®äº‹ä»¶:', fname);

      downloadBtn.addEventListener('click', (e) => {
        e.preventDefault();
        e.stopPropagation();
        console.log('ä¸‹è½½æŒ‰é’®ç‚¹å‡»:', fname);
        downloadImage(dataUrl, fname);
      });

      // æ·»åŠ æ‚¬åœæ•ˆæœ
      downloadBtn.addEventListener('mouseenter', () => {
        downloadBtn.style.background = 'rgba(99, 102, 241, 0.9)';
        downloadBtn.style.transform = 'scale(1.1)';
      });

      downloadBtn.addEventListener('mouseleave', () => {
        downloadBtn.style.background = 'rgba(0, 0, 0, 0.8)';
        downloadBtn.style.transform = 'scale(1)';
      });
    } else {
      console.error('ä¸‹è½½æŒ‰é’®æœªæ‰¾åˆ°');
    }
  });

  previewContent.appendChild(imageGrid);
}

// ä¸‹è½½å›¾ç‰‡å‡½æ•°
function downloadImage(dataUrl, filename) {
  const link = document.createElement('a');
  link.href = dataUrl;
  link.download = filename;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}

// åœ¨åº•éƒ¨æ˜¾ç¤ºå®Œæ•´ç»“æœï¼ˆä¿æŒåŸæœ‰åŠŸèƒ½ï¼‰
function renderCards(list) {
  const grid = $('grid');
  const res = $('result');
  grid.innerHTML = '';
  list.forEach((it, idx) => {
    const dataUrl = buildDataURL(it);
    if (!dataUrl) return;
    const fname = safeName(it.output_filename || `image_${idx + 1}.png`);
    const card = document.createElement('div');
    card.className = 'card';
    card.innerHTML = `
      <div class="card-image">
        <img src="${dataUrl}" alt="${fname}" loading="lazy" decoding="async" />
      </div>
      <div class="card-meta">
        <div class="card-desc">çŸ¥è¯†å¡ç‰‡å·²ç”Ÿæˆå®Œæˆ</div>
        <div class="card-filename">æ–‡ä»¶åå»ºè®®ï¼š${fname}ï¼ˆå³é”®å›¾ç‰‡å¦å­˜ä¸ºï¼‰</div>
      </div>
    `;
    grid.appendChild(card);
  });
}

// DOM åˆå§‹åŒ–
document.addEventListener('DOMContentLoaded', function() {
  const f = $('f');
  const st = $('status');
  const txt = $('stxt');
  const fill = $('fill');
  const done = $('done');
  const res = $('result');
  const btnReset = $('reset');
  const kw = $('kw');
  const aud = $('aud');
  const cnt = $('cnt');
  const sty = $('sty');
  const goBtn = $('go');

  const formPanel = document.querySelector('.form-panel');

  function showStatus(s, p) { 
    st.classList.remove('hidden'); 
    txt.textContent = s; 
    fill.style.width = p; 
  }

  function disableForm() {
    formPanel.classList.add('form-disabled');
    goBtn.classList.add('loading');
    goBtn.textContent = 'ç”Ÿæˆä¸­...';
  }

  function enableForm() {
    formPanel.classList.remove('form-disabled');
    goBtn.classList.remove('loading');
    goBtn.textContent = 'âœ¨ å¼€å§‹åˆ›ä½œ';
  }

  // æ ·å¼é€‰æ‹©å™¨äº¤äº’
  document.querySelectorAll('.style-option').forEach(option => {
    option.addEventListener('click', () => {
      // å¦‚æœæ­£åœ¨ç”Ÿæˆä¸­ï¼Œä¸å…è®¸ä¿®æ”¹
      if (goBtn.classList.contains('loading')) return;

      document.querySelectorAll('.style-option').forEach(opt => opt.classList.remove('selected'));
      option.classList.add('selected');
      sty.value = option.dataset.style;
    });
  });

  btnReset.addEventListener('click', () => location.reload());

  f.addEventListener('submit', async (e) => {
    e.preventDefault();
    if (!aud.value) {
      alert('è¯·é€‰æ‹©ç›®æ ‡å—ä¼—');
      return;
    }  
    // ç¦ç”¨è¡¨å•è€Œä¸æ˜¯éšè—
    disableForm(); 
    showStatus('å‡†å¤‡æç¤ºè¯...', '20%');

    const payload = {
      keywords: kw.value,
      audience: aud.value,
      count: Math.max(1, Math.min(6, Number(cnt.value || 3))),
      style: sty.value
    };

    try {
      showStatus('æ­£åœ¨ç”Ÿæˆå›¾ç‰‡...', '55%');
      const r = await fetch(POST_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      if (!r.ok) throw new Error(await r.text() || ('HTTP ' + r.status));
      showStatus('æ¸²æŸ“ç»“æœä¸­...', '100%');

      const data = await r.json();
      if (!Array.isArray(data) || data.length === 0) throw new Error('No images returned');

      // ä¿å­˜å…¨å±€å›¾ç‰‡æ•°æ®
      generatedImages = data;

      // æ›´æ–°å³ä¾§é¢„è§ˆåŒºåŸŸ
      updatePreview(data);

      // éšè—çŠ¶æ€ï¼Œæ˜¾ç¤ºå®Œæˆä¿¡æ¯
      done.classList.remove('hidden');

      // åŒæ—¶ä¿ç•™åº•éƒ¨ç»“æœåŒºåŸŸï¼ˆå¯é€‰ï¼‰
      // res.classList.remove('hidden');
      // renderCards(data);
    } catch (err) {
      alert('ä»»åŠ¡å¤±è´¥: ' + err.message);
    } finally {
      // æ— è®ºæˆåŠŸæˆ–å¤±è´¥ï¼Œéƒ½é‡æ–°å¯ç”¨è¡¨å•
      enableForm();
    }
  });
});
</script>
