parameters.jsCode:
// 获取输入数据
const allChunks = $input.all();
const content = $('AI: 生成小红书文案').first().json.output;
const title = $('AI:小红书内容分段专家').first().json.output.pages[0].title;

// 分别提取成功的图片和错误记录
const fileTokens = [];
const errorRecords = [];
allChunks.forEach(item => {
  if (item.json && item.json.data && item.json.data.file_token) {
    fileTokens.push({
      "file_token": item.json.data.file_token
    });
  } else if (item.json && item.json.error_type) {
    // 错误记录
    errorRecords.push({
      "error_type": item.json.error_type,
      "page_info": item.json.page_info,
      "failed_prompt": item.json.failed_prompt,
      "error_details": item.json.error_details
    });
  }
});

console.log("提取的file_tokens:", fileTokens);
console.log("提取的错误记录:", errorRecords);

// 计算统计信息
const successCount = fileTokens.length;
const failureCount = errorRecords.length;
const totalPages = successCount + failureCount;

const finalDataObject= {
    "fields": {
    "状态": "已完成",
    "小红书标题": title,
    "生成时间": $now.toMillis(),
    "小红书封面": fileTokens,
    "小红书文案": content,
    "备注": JSON.stringify(errorRecords),
    "总页数": totalPages,
    "成功数量": successCount,
    "失败数量": failureCount
  }
}

return {
  finalJsonPayload: JSON.stringify(finalDataObject)
}

