    }

    // å¦‚æœæ²¡æœ‰è¯†åˆ«çš„å­—æ®µï¼Œæ˜¾ç¤ºåŸå§‹æ•°æ®
    if (!data.weather && !data.recommendations && !data.response && !data.message) {
      const rawItem = createResultItem(
