}

// åˆ›å»ºç»“æœé¡¹
function createResultItem(title, content, status = 'success') {
  const item = document.createElement('div');
  item.className = 'result-item';

  const statusClass = `status-${status}`;
  const statusText = {
    success: 'æˆåŠŸ',
    warning: 'æ³¨æ„',
    error: 'é”™è¯¯'
  }[status] || 'ä¿¡æ¯';

  item.innerHTML = `
    <div class="result-item-title">
      ${title}
      <span class="status-badge ${statusClass}">${statusText}</span>
    </div>
    <div class="result-item-content">${formatContent(content)}</div>
  `;

  return item;
}

// æ ¼å¼åŒ–å†…å®¹
function formatContent(content) {
  if (typeof content === 'string') {
    // å°†æ¢è¡Œç¬¦è½¬æ¢ä¸ºHTMLæ¢è¡Œ
    return content.replace(/\n/g, '<br>');
  }
  return String(content);
}

// æ ¼å¼åŒ–å¤©æ°”ä¿¡æ¯
function formatWeatherInfo(weather) {
  if (typeof weather === 'string') return weather;

  let result = '';
  if (weather.city) result += `ğŸ“ ${weather.city}<br>`;
  if (weather.temperature) result += `ğŸŒ¡ï¸ æ¸©åº¦ï¼š${weather.temperature}<br>`;
  if (weather.condition) result += `â˜ï¸ å¤©æ°”ï¼š${weather.condition}<br>`;
  if (weather.humidity) result += `ğŸ’§ æ¹¿åº¦ï¼š${weather.humidity}<br>`;
  if (weather.wind) result += `ğŸ’¨ é£åŠ›ï¼š${weather.wind}<br>`;

  return result || 'å¤©æ°”ä¿¡æ¯è·å–æˆåŠŸ';
}

// æ ¼å¼åŒ–æ¨èä¿¡æ¯
function formatRecommendations(recommendations) {
  if (typeof recommendations === 'string') return recommendations;

  if (Array.isArray(recommendations)) {
    return recommendations.map((item, index) => {
      if (typeof item === 'string') {
        return `${index + 1}. ${item}`;
      } else if (item.name) {
        let result = `${index + 1}. ${item.name}`;
        if (item.address) result += ` - ${item.address}`;
        if (item.rating) result += ` (è¯„åˆ†: ${item.rating})`;
        return result;
      }
      return `${index + 1}. ${JSON.stringify(item)}`;
    }).join('<br>');
  }

  return JSON.stringify(recommendations, null, 2);
}

