function syncPanelHeights() {
  const queryPanel = document.querySelector('.query-panel');
  const resultPanel = document.querySelector('.result-panel');
  const resultContent = document.querySelector('.result-content');

  if (queryPanel && resultPanel && resultContent) {
    // è·å–å·¦ä¾§é¢æ¿çš„å®é™…é«˜åº¦
    const queryPanelHeight = queryPanel.offsetHeight;

    // è®¾ç½®å³ä¾§é¢æ¿é«˜åº¦åŒ¹é…å·¦ä¾§
    resultPanel.style.height = queryPanelHeight + 'px';

    // è®¡ç®—å³ä¾§å†…å®¹åŒºåŸŸçš„å¯ç”¨é«˜åº¦
    const resultHeader = document.querySelector('.result-header');
    const headerHeight = resultHeader ? resultHeader.offsetHeight : 60;
    const availableHeight = queryPanelHeight - headerHeight - 64; // 64pxä¸ºä¸Šä¸‹padding

    // è®¾ç½®å†…å®¹åŒºåŸŸé«˜åº¦
    resultContent.style.maxHeight = availableHeight + 'px';
