/**
 * 将Markdown文本转换为纯文本
 * @param {string} markdown - 输入的Markdown格式文本
 * @returns {string} - 转换后的纯文本字符串
 */
function markdownToText(markdown) {
    if (!markdown) return '';

    let text = markdown;

    // 移除代码块
    text = text.replace(/```[\s\S]*?```/g, '');
    text = text.replace(/`([^`]+)`/g, '$1');

    // 移除标题符号
    text = text.replace(/^#{1,6}\s+/gm, '');

    // 移除粗体和斜体
    text = text.replace(/[*_]{2}([^*_]+)[*_]{2}/g, '$1'); // 粗体
    text = text.replace(/[*_]([^*_]+)[*_]/g, '$1'); // 斜体

    // 处理链接
    text = text.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1'); // [文本](链接)
    text = text.replace(/\[([^\]]+)\]\[[^\]]*\]/g, '$1'); // [文本][引用]

    // 移除图片标记
    text = text.replace(/!\[([^\]]*?)\]\([^)]+\)/g, '$1');

    // 处理各种非标准格式的图片标记
    text = text.replace(/!图片\s*\)/g, '');
    text = text.replace(/!图片\s*$/g, '');
    text = text.replace(/!图片[\s\S]*?\)/g, '');
    text = text.replace(/^\s*\)\s*$/gm, '');
    text = text.replace(/^\s*$/gm, '');

    // 处理列表
    text = text.replace(/^[\s]*[\-*+]\s+/gm, ''); // 无序列表
    text = text.replace(/^[\s]*\d+\.\s+/gm, ''); // 有序列表

    // 处理引用
    text = text.replace(/^\s*>\s+/gm, '');

    // 处理水平分割线
    text = text.replace(/^\s*[-*_]{3,}\s*$/gm, '\n');

    // 移除SVG相关标签及其内容
    text = text.replace(/<svg[^>]*>(([\s\S]*?)<\/svg>)?/gi, '');

    // 增强SVG标签清理
    const svgElements = 'svg|rect|circle|path|line|polygon|polyline|g|defs|use|symbol|ellipse|text|image|clipPath|mask|pattern|filter|marker|linearGradient|radialGradient|stop|tspan|textPath|animate|animateMotion|animateTransform|set|foreignObject|switch|desc|title|metadata';
    text = text.replace(new RegExp(`<(${svgElements})(\\s[^>]*)?\\/?>`,'gi'), '');
    text = text.replace(new RegExp(`<\\/(${svgElements})>`,'gi'), '');

    // 移除单独的SVG标签片段
    text = text.replace(/[\s']+(?:rect|circle|path|g|svg|defs|use|symbol)\s*\/?/gi, ' ');
    text = text.replace(/[\s']+\/(?:rect|circle|path|g|svg|defs|use|symbol)\s*/gi, ' ');

    // 移除括号内的SVG标签片段
    text = text.replace(/\([^\)]*?(?:rect|circle|path|g|svg)[^\)]*?\)/gi, '');

    // 清理SVG属性和标签片段
    text = text.replace(/['"\s]+(fill|x|y|width|height|rx|ry|cx|cy|r|d|points|viewBox|xmlns)=['"]?[^'">\s]*['"]?/gi, ' ');
    text = text.replace(/\s+(?:\/)?(?:rect|circle|path|g|svg|defs|use|symbol|ellipse|text)(?:\s+|\/)/gi, ' ');
    text = text.replace(/\s+\/(?:rect|circle|path|g|svg|defs|use|symbol|ellipse|text)(?:\s+|$)/gi, ' ');

    // 移除SVG和HTML常见属性
    const commonAttributes = 'xmlns|version|viewBox|width|height|class|style|id|fill|stroke|d|points|transform|x|y|cx|cy|r|x1|y1|x2|y2|dx|dy|offset|gradientUnits|spreadMethod|patternUnits|patternTransform|markerWidth|markerHeight|refX|refY|orient|font-size|font-family|text-anchor|alignment-baseline|href|xlink:href|preserveAspectRatio';
    text = text.replace(new RegExp(`\\s+(${commonAttributes})=[\"\']([^\"\']*)[\"\'](?:\\s+|$)`,'gi'), ' ');

    // 移除data URI和编码内容
    text = text.replace(/data:(?:[^;]+;)*(?:base64,)?[a-zA-Z0-9+/]+={0,2}/g, '');
    text = text.replace(/%[0-9a-fA-F]{2}/g, ' ');

    // 最终清理：移除任何剩余的SVG相关内容
    text = text.replace(/\([^\)]*(?:svg|rect|g|path|circle)[^\)]*\)/gi, '');
    text = text.replace(/'[^']*(?:svg|rect|g|path|circle)[^']*'/gi, '');
    text = text.replace(/"[^"]*(?:svg|rect|g|path|circle)[^"]*"/gi, '');
    text = text.replace(/(?:\/)?(?:svg|rect|g|path|circle|defs|use|symbol)(?:\/)?/gi, '');

    // 移除其他HTML标签
    text = text.replace(/<[^>]+>/g, '');

    // 处理转义字符
    text = text.replace(/\\([\\`*_{}\[\]()#+\-.!])/g, '$1');

    // 清理多余的空行
    text = text.replace(/\n\s*\n/g, '\n\n');
    text = text.replace(/^\s+|\s+$/g, '');

    return text;
}

/**
 * 将文本按指定长度分段
 * @param {string} text - 输入文本
 * @param {number} segmentLength - 每段文本的最大长度
 * @returns {string[]} - 分段后的文本数组
 */
function splitTextIntoSegments(text, segmentLength = 1800) {
    const segments = [];
    let remainingText = text;

    while (remainingText.length > 0) {
        if (remainingText.length <= segmentLength) {
            segments.push(remainingText);
            break;
        }

        let endIndex = segmentLength;
        // 尝试在句子或段落边界处分段
        const nextParagraph = remainingText.indexOf('\n\n', endIndex - 100);
        const nextSentence = remainingText.indexOf('. ', endIndex - 100);

        if (nextParagraph !== -1 && nextParagraph < endIndex + 100) {
            endIndex = nextParagraph;
        } else if (nextSentence !== -1 && nextSentence < endIndex + 100) {
            endIndex = nextSentence + 1;
        }

        segments.push(remainingText.substring(0, endIndex).trim());
        remainingText = remainingText.substring(endIndex).trim();
    }

    return segments;
}

// 获取所有输入数据项
const inputItems = $input.all();
const results = [];

// 遍历处理每个输入数据项
for (let i = 0; i < inputItems.length; i++) {
    const currentItem = inputItems[i];
    const markdownInput = currentItem.json.data;

    if (!markdownInput) {
        // 如果没有数据，跳过这个项目
        continue;
    }

    const plainText = markdownToText(markdownInput);
    const textSegments = splitTextIntoSegments(plainText);

    // 将文本段落转换为规范的block对象格式
    const blocks = textSegments.map(segment => ({
        object: 'block',
        type: 'paragraph',
        paragraph: {
            rich_text: [
                {
                    type: 'text',
                    annotations: {
                        bold: false,
                        strikethrough: false,
                        underline: false,
                        italic: false,
                        code: false,
                        color: 'default'
                    },
                    text: {
                        content: segment
                    }
                }
            ]
        }
    }));

    // 只返回blocks字段
    results.push({
        json: {
            blocks: blocks
        }
    });
}

return results;

const array1 = $input.first().json.biz;
const array2 = $input.first().json.name;
const combined = [];

for (let i = 0; i < array1.length; i++) {
  combined.push({
    key: array1[i],
    value: array2[i]
  });
}

return combined.map(item => ({ json: item }));
